package assignment1;

public class Ques1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=7;
		evenodd(a);	
	}
	static void evenodd(int a) {
		if(a%2==0)
		{
			System.out.println(a+" is even");	
		}
		else
		{
			System.out.println(a+" is odd");
		}
	}
	
	}


